package vo;

public class ScreeningVo {

	private int idx;
	private String theater_code;
	private String name;
	private int screening_year;
	private int screening_month;
	private int screening_date;
	private String screening_time;

	public ScreeningVo() {
		// TODO Auto-generated constructor stub
	}

	public int getIdx() {
		return idx;
	}

	public void setIdx(int idx) {
		this.idx = idx;
	}

	public String getTheater_code() {
		return theater_code;
	}

	public void setTheater_code(String theater_code) {
		this.theater_code = theater_code;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getScreening_year() {
		return screening_year;
	}

	public void setScreening_year(int screening_year) {
		this.screening_year = screening_year;
	}

	public int getScreening_month() {
		return screening_month;
	}

	public void setScreening_month(int screening_month) {
		this.screening_month = screening_month;
	}

	public int getScreening_date() {
		return screening_date;
	}

	public void setScreening_date(int screening_date) {
		this.screening_date = screening_date;
	}

	public String getScreening_time() {
		return screening_time;
	}

	public void setScreening_time(String screening_time) {
		this.screening_time = screening_time;
	}

}
